st.body:
;
(*
最大脉冲
版本：V0.0
*)
CP:=GetIECInterval(1);
NC:=TIME_TO_DWORD(DLYTIME)/CP;(*20120323_1*)
(*
IF NC >65000 THEN
    NC :=65000;
END_IF;
*)
IF (NOT PRES1) AND S1 THEN
	pdelay:=1;
	SO:=1;
END_IF
IF pdelay THEN
	CT:=CT+1;
	IF NOT(S1) THEN
		CY:=CT;
	END_IF	
END_IF
IF (NOT(S1)  AND CY<NC ) OR (CT>NC ) THEN
	SO:=0;
	pdelay:=0;
	CT:=0;
	CY:=0;
END_IF

PRES1:=S1;
