pou.interface:
FUNCTION ROUNDUP : WORD
VAR
		temp(2112): WORD := 0;
		temp1(2112): WORD := 0;
END_VAR
VAR_INPUT
		p1(2113): REAL := 0;
END_VAR
VAR_IN_OUT
END_VAR

st.body:
;
temp:= REAL_TO_WORD(p1);
temp1:= REAL_TO_WORD(p1+0.5);

IF (P1-WORD_TO_REAL(TEMP))<0.00001 THEN
	ROUNDUP:=TEMP;
ELSE
	ROUNDUP:=TEMP1;
END_IF
